import { Box, Button } from '@material-ui/core';
import React from 'react';
import corIgreja from 'src/utils/coresIgreja';
import '@fontsource/roboto-mono'; // PadrÃµes para peso 400.
import '@fontsource/rubik';
import IconButton from '@mui/material/IconButton';
import { BiCaretLeft, BiCaretRight } from 'react-icons/bi';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import Typography from '@mui/material/Typography';
import TableContainer from '@mui/material/TableContainer';
import axios from 'axios';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import Image from 'next/image';
//--------------------------------------------------------------------------
const bannerBlurHash =
  'data:image/webp;base64,';
//--------------------------------------------------------------------------

const theme = createTheme();
theme.typography.hs4 = {
  fontWeight: 'normal',
  fontSize: '9px',
  '@media (min-width:350px)': {
    fontSize: '10px',
  },
  [theme.breakpoints.up('md')]: {
    fontSize: '11px',
  },
};
theme.typography.hs3 = {
  fontWeight: 'normal',
  fontSize: '11px',
  '@media (min-width:350px)': {
    fontSize: '12px',
  },
  '@media (min-width:400px)': {
    fontSize: '13px',
  },
  [theme.breakpoints.up('md')]: {
    fontSize: '14px',
  },
};
theme.typography.hs2 = {
  fontWeight: 'normal',
  fontSize: '10px',
  '@media (min-width:350px)': {
    fontSize: '11px',
  },
  '@media (min-width:400px)': {
    fontSize: '12px',
  },
  [theme.breakpoints.up('md')]: {
    fontSize: '13px',
  },
};
function createListaIgrejas(label, value) {
  return {
    label,
    value,
  };
}
function Funcao({ setOpenInscritos, nomesIgrejas, evento }) {
  const [contNumeroIgreja, setContNumeroIgreja] = React.useState(0);
  const [listaInscritos, setListaInscritos] = React.useState([]);
  const [newListaInscritos, setNewListaInscritos] = React.useState([]);
  const [adultos, setAdultos] = React.useState(0);
  const [criancas1, setCriancas1] = React.useState(0);
  const [criancas2, setCriancas2] = React.useState(0);
  const [listaIgrejaF, setListaIgrejaF] = React.useState({
    label: 'TODAS AS IGREJA',
    value: 0,
  });

  React.useEffect(async () => {
    try {
      const url = `${window.location.origin}/api/consultaInscritosEventosTipo/${evento.Evento}`;
      const res = await axios.get(url);

      if (res.data) {
        setListaInscritos(res.data);
      }
    } catch (err) {
      console.log(err);
    }
  }, []);
  React.useEffect(async () => {
    if (nomesIgrejas.length) {
      const newListaIgreja = [
        {
          label: 'TODAS AS IGREJAS',
          value: 0,
        },
      ];
      nomesIgrejas.map((val, index) => {
        newListaIgreja[index + 1] = createListaIgrejas(val.nomeNucleo, index);
        return 0;
      });

      if (newListaIgreja.length) setListaIgrejaF(newListaIgreja);
    }
  }, [nomesIgrejas]);
  React.useEffect(async () => {
    let adultoF = 0;
    let c1F = 0;
    let c2F = 0;
    if (listaIgrejaF.length && listaInscritos) {
      if (listaIgrejaF[contNumeroIgreja].label === 'TODAS AS IGREJAS') {
        adultoF = listaInscritos
          .map((item) => {
            if (item.qtyAdultos !== undefined) return item.qtyAdultos;
            return 0;
          })
          .reduce((prev, curr) => prev + curr, 0);
        c1F = listaInscritos
          .map((item) => {
            if (item.qtyCriancas1 !== undefined) return item.qtyCriancas1;
            return 0;
          })
          .reduce((prev, curr) => prev + curr, 0);
        c2F = listaInscritos
          .map((item) => {
            if (item.qtyCriancas2 !== undefined) return item.qtyCriancas2;
            return 0;
          })
          .reduce((prev, curr) => prev + curr, 0);
        setNewListaInscritos(listaInscritos);
      } else {
        const listaInscritos2 = listaInscritos?.filter(
          (val) => val.Igreja === listaIgrejaF[contNumeroIgreja].label,
        );

        adultoF = listaInscritos2
          ?.map((item) => {
            if (item.qtyAdultos !== undefined) return item.qtyAdultos;
            return 0;
          })
          .reduce((prev, curr) => prev + curr, 0);
        c1F = listaInscritos2
          .map((item) => {
            if (item.qtyCriancas1 !== undefined) return item.qtyCriancas1;
            return 0;
          })
          .reduce((prev, curr) => prev + curr, 0);
        c2F = listaInscritos2
          .map((item) => {
            if (item.qtyCriancas2 !== undefined) return item.qtyCriancas2;
            return 0;
          })
          .reduce((prev, curr) => prev + curr, 0);
        setNewListaInscritos(listaInscritos2);
      }
      setAdultos(adultoF);
      setCriancas1(c1F);
      setCriancas2(c2F);
    }
  }, [contNumeroIgreja, listaIgrejaF, listaInscritos]);
  //= ===================================================================
  const handleIncIgreja = () => {
    let contDistritoAtual = contNumeroIgreja + 1;

    if (contDistritoAtual > listaIgrejaF.length - 1) contDistritoAtual = 0;
    setContNumeroIgreja(contDistritoAtual);
  };

  const handleDecIgreja = () => {
    let contDistritoAtual = contNumeroIgreja - 1;

    if (contDistritoAtual < 0) contDistritoAtual = listaIgrejaF.length - 1;
    setContNumeroIgreja(contDistritoAtual);
  };
  //= ===================================================================

  //----------------------------------------------------------------

  return (
    <Box
      height="100vh"
      minHeight={600}
      minWidth={300}
      width="100vw"
      bgcolor={corIgreja.principal}
    >
      <Box
        width="100%"
        height="100%"
        minWidth={300}
        mt={0}
        display="flex"
        justifyContent="center"
        alignItems="center"
      >
        <Box height="100%" width="100%">
          <Box
            width="100%"
            display="flex"
            justifyContent="center"
            alignItems="end"
            fontFamily="Fugaz One"
            color="white"
            height="5%"
          >
            {evento ? evento.Evento.toLocaleUpperCase() : ''}
          </Box>
          <Box
            alignItems="center"
            justifyContent="center"
            height="8%"
            width="100%"
            display="flex"
          >
            <Box
              bgcolor={corIgreja.principal2}
              color="#000"
              justifyContent="center"
              width="90%"
              display="flex"
              height={38}
            >
              <Box ml={0} width="100%" display="flex">
                <Box
                  width="10%"
                  display="flex"
                  justifyContent="center"
                  alignItems="center"
                >
                  <IconButton
                    color="primary"
                    aria-label="upload picture"
                    component="span"
                    onClick={() => {
                      handleDecIgreja();
                    }}
                  >
                    <BiCaretLeft size={30} color="#f0f0f0" />
                  </IconButton>
                </Box>
                <Box
                  width="100%"
                  ml={0}
                  color={corIgreja.textoS}
                  display="flex"
                  justifyContent="center"
                  alignItems="center"
                  fontSize="16px"
                  sx={{ fontFamily: 'Fugaz One' }}
                >
                  <Box ml={2} mr={2}>
                    <ThemeProvider theme={theme}>
                      <Typography variant="hs2">
                        {listaIgrejaF[contNumeroIgreja]
                          ? listaIgrejaF[contNumeroIgreja].label
                          : 'aguarde...'}
                      </Typography>
                    </ThemeProvider>
                  </Box>
                </Box>
                <Box
                  width="10%"
                  display="flex"
                  justifyContent="flex-end"
                  alignItems="center"
                >
                  <IconButton
                    color="primary"
                    aria-label="upload picture"
                    component="span"
                    onClick={() => {
                      handleIncIgreja();
                    }}
                  >
                    <BiCaretRight size={30} color="#f0f0f0" />
                  </IconButton>
                </Box>
              </Box>
            </Box>
          </Box>
          <Box
            justifyContent="center"
            width="100%"
            height="10%"
            display="flex"
            alignItems="center"
          >
            <Box
              display="flex"
              alignItems="center"
              justifyContent="center"
              bgcolor="white"
              width="90%"
              height="100%"
              borderRadius={6}
            >
              <Box>
                <Box
                  ml={2}
                  width="100%"
                  textAlign="center"
                  fontFamily="Fugaz ONE"
                  color="black"
                >
                  TOTAL DE INSCRITOS:
                </Box>
                <ThemeProvider theme={theme}>
                  <Typography variant="hs2">
                    <Box
                      mt={2}
                      width="100%"
                      display="flex"
                      justifyContent="center"
                      fontFamily="arial"
                      color="black"
                    >
                      ADULTOS:{' '}
                      <Box ml={1} fontFamily="Fugaz ONE" color="blue">
                        {adultos}
                      </Box>
                      <Box ml={3} display="flex">
                        0 Ã  6 anos:{' '}
                        <Box ml={1} fontFamily="Fugaz ONE" color="blue">
                          {criancas2}
                        </Box>
                      </Box>
                      <Box ml={3} display="flex">
                        7 Ã  11 anos:{' '}
                        <Box ml={1} fontFamily="Fugaz ONE" color="blue">
                          {criancas1}
                        </Box>
                      </Box>
                    </Box>
                  </Typography>
                </ThemeProvider>
              </Box>
            </Box>
          </Box>
          <Box height="2%" />
          <Box
            justifyContent="center"
            alignItems="start"
            width="100%"
            height="65%"
            display="flex"
          >
            <Box bgcolor="white" height="100%" width="90%" borderRadius={6}>
              <TableContainer sx={{ height: '100%' }}>
                {newListaInscritos && newListaInscritos.length ? (
                  <Box
                    display="flex"
                    justifyContent="center"
                    alignItems="start"
                    width="90vw"
                    height="100%"
                  >
                    <List sx={{ width: '100%', maxWidth: 360 }}>
                      {newListaInscritos?.map((row, index) => (
                        <ListItem key={index} alignItems="flex-start">
                          <ListItemAvatar>
                            {row.foto !== null && row.foto !== undefined ? (
                              <Avatar
                                src=""
                                alt="User"
                                style={{
                                  width: 50,
                                  height: 50,
                                }}
                              >
                                {row.foto.slice(0, 1) === 'h' ? (
                                  <Image
                                    src={row.foto}
                                    layout="fill"
                                    objectFit="contain"
                                    loading="eager"
                                    placeholder="blur"
                                    blurDataURL={bannerBlurHash}
                                  />
                                ) : null}
                              </Avatar>
                            ) : (
                              <Avatar
                                src=""
                                alt="User"
                                style={{
                                  width: 50,
                                  height: 50,
                                }}
                              />
                            )}
                          </ListItemAvatar>
                          <Box>
                            <ListItemText style={{ marginTop: 20 }}>
                              <Box
                                style={{
                                  display: 'flex',
                                  marginLeft: 10,
                                  fontFamily: 'Fugaz One',
                                  fontSize: '12px',
                                  color: '#000',
                                }}
                              >
                                <ThemeProvider theme={theme}>
                                  <Typography variant="hs2">
                                    {row.Nome.length > 30
                                      ? row.Nome.substring(
                                          0,
                                          row.Nome.lastIndexOf(' '),
                                        ).toUpperCase()
                                      : row.Nome.toUpperCase()}
                                  </Typography>
                                </ThemeProvider>
                              </Box>

                              <Box
                                style={{
                                  display: 'flex',
                                  marginLeft: 10,
                                  fontFamily: 'Rubik',
                                  fontWeight: 'bold',
                                  fontSize: '12px',
                                  color: '#000',
                                }}
                              >
                                <Box ml={0.2} display="flex">
                                  <ThemeProvider theme={theme}>
                                    <Typography variant="hs4">
                                      <Box display="flex">
                                        Adultos:{' '}
                                        <Box color="blue" ml={0.5}>
                                          {row.qtyAdultos ? row.qtyAdultos : 0}
                                        </Box>
                                        <Box ml={2} display="flex">
                                          0 Ã  6 anos:{' '}
                                          <Box color="blue" ml={0.5}>
                                            {row.qtyCriancas2
                                              ? row.qtyCriancas2
                                              : 0}
                                          </Box>
                                        </Box>
                                        <Box ml={2} display="flex">
                                          7 Ã  11 anos:{' '}
                                          <Box color="blue" ml={0.5}>
                                            {row.qtyCriancas1
                                              ? row.qtyCriancas1
                                              : 0}
                                          </Box>
                                        </Box>
                                      </Box>
                                    </Typography>
                                  </ThemeProvider>
                                </Box>
                              </Box>
                            </ListItemText>
                          </Box>
                        </ListItem>
                      ))}
                    </List>
                  </Box>
                ) : null}
              </TableContainer>
            </Box>
          </Box>
          <Box
            display="flex"
            justifyContent="center"
            alignItems="center"
            height="10%"
          >
            <Button
              onClick={() => {
                setOpenInscritos(false);
              }}
              style={{
                background: corIgreja.tercenaria,
                color: 'white',
                fontFamily: 'Fugaz One',
                width: '70%',
              }}
              variant="contained"
              severity="success"
              //   endIcon={<TouchAppIcon />}
            >
              Fechar
            </Button>
          </Box>
        </Box>
      </Box>
    </Box>
  );
}

export default Funcao;
